class Cookie {
    static parse(str, name) {
        if (str && str.length > 0) {
            const list = {};
            str.replace(" ", "").split(";").forEach(pair => {
                const parts = pair.split("=");
                list[parts[0]] = decodeURI(parts[1]);
            });

            if (name && list.hasOwnProperty(name)) {
                return list[name];
            }
            return null;
        }
        return null;
    }
}

module.exports = Cookie;