const MongoClient = require("mongodb").MongoClient;

module.exports = new Promise(resolve => {
    MongoClient.connect("mongodb://localhost:27017", {
        autoReconnect: true,
        poolSize: 10,
        useNewUrlParser: true
    }, (error, client) => {
        if (error) {
            console.log(error);
            return;
        }
        resolve(client.db("database"));
    });
});