const ObjectId = require("mongodb").ObjectId;
const fs = require("fs");
const channel = require("class/channel.js");

module.exports = async (session, query) => {
    if (await session.set()) {
        const { guild_id, emoji_id } = query;

        if (await channel.isOwner(guild_id, session.user_id)) {
            const db = await require("class/database.js");
            const result = await db.collection("emojis").deleteOne({ _id: emoji_id, guild_id: ObjectId(guild_id) });

            if (result.deletedCount === 1) {
                try {
                    fs.unlinkSync(`emojis/${emoji_id}`);
                } catch (error) {
                    console.log(error);
                }
                const find = await db.collection("guilds").findOne({ _id: ObjectId(guild_id) }, { projection: { _id: 0, channels: 1 } });

                if (find !== null) {
                    return {
                        status: true,
                        type: "GUILD_EMOJI_DELETE",
                        access: find.channels[0].toString(),
                        guild_id: guild_id,
                        emoji_id: emoji_id
                    }
                } else {
                    return {
                        status: true,
                        type: "GUILD_EMOJI_DELETE",
                        guild_id: guild_id,
                        emoji_id: emoji_id
                    }
                }
            } else {
                return {
                    status: true
                }
            }
        } else {
            return {
                status: false,
                error: "Missing permissions"
            }
        }
    } else {
        return {
            status: false
        }
    }
}