const ObjectId = require("mongodb").ObjectId;
const channel = require("class/channel.js");
const check = require("class/check.js");

module.exports = async (session, message) => {
    if (await session.set()) {
        const { invite } = message;

        if (check.invite(invite)) {
            const db = await require("class/database.js");
            const find = await db.collection("invites").findOne({ _id: invite }, { projection: { _id: 0, guild_id: 1 } });

            if (find !== null) {
                const guild_id = find.guild_id.toString();

                if (await channel.isOwner(guild_id, session.user_id)) {
                    const result = await db.collection("invites").deleteOne({ _id: invite, guild_id: ObjectId(guild_id) });

                    if (result.deletedCount === 1) {
                        return {
                            status: true
                        }
                    } else {
                        return {
                            status: false,
                            error: "Failed to delete invite"
                        }
                    }
                } else {
                    return {
                        status: false,
                        error: "Missing permissions"
                    }
                }
            } else {
                return {
                    status: false,
                    error: "Invite does not exist"
                }
            }
        } else {
            return {
                status: false,
                error: "Invalid invite"
            }
        }
    } else {
        return {
            status: false
        }
    }
}