const ObjectId = require("mongodb").ObjectId;
const channel = require("class/channel.js");

module.exports = async (session, query, message) => {
    if (await session.set()) {
        const { guild_id, emoji_id } = query;

        if (await channel.isOwner(guild_id, session.user_id)) {
            const { name } = message;

            const db = await require("class/database.js");
            const result = await db.collection("emojis").updateOne({ _id: emoji_id, guild_id: ObjectId(guild_id) }, { $set: { name: name } });

            if (result.modifiedCount === 1) {
                return {
                    status: true
                }
            } else {
                return {
                    status: false,
                    error: "Failed to rename emoji"
                }
            }
        } else {
            return {
                status: false,
                error: "Missing permissions"
            }
        }
    } else {
        return {
            status: false
        }
    }
}