const ObjectId = require("mongodb").ObjectId;
const channel = require("class/channel.js");

module.exports = async (session, query) => {
    if (await session.set()) {
        const { channel_id } = query;

        if (channel.isValidId(channel_id)) {
            const db = await require("class/database.js");
            const result = await db.collection("dm_channels").updateOne({ _id: ObjectId(channel_id), recipient: ObjectId(session.user_id) }, { $set: { accepted: true } });

            if (result.modifiedCount === 1) {
                return {
                    status: true,
                    type: "DM_ACCEPT",
                    channel_id: channel_id
                }
            } else {
                return {
                    status: false,
                    error: "Failed to accept friend request"
                }
            }
        } else {
            return {
                status: false,
                error: "Unknown channel"
            }
        }
    } else {
        return {
            status: false
        }
    }
}