const ObjectId = require("mongodb").ObjectId;
const channel = require("class/channel.js");

module.exports = async (session, query) => {
    if (await session.set()) {
        const { channel_id } = query;

        if (channel.isValidId(channel_id)) {
            const db = await require("class/database.js");
            const result = await db.collection("dm_channels").deleteOne({ _id: ObjectId(channel_id), $or: [ { client: ObjectId(session.user_id) }, { recipient: ObjectId(session.user_id) } ], accepted: false });

            if (result.deletedCount === 1) {
                return {
                    status: true,
                    type: "DM_CANCEL",
                    channel_id: channel_id
                }
            } else {
                return {
                    status: false,
                    error: "Failed to cancel friend request"
                }
            }
        } else {
            return {
                status: false,
                error: "Unknown channel"
            }
        }
    } else {
        return {
            status: false
        }
    }
}